const allSideMenu=document.querySelectorAll("#sidebar .side-menu.top li a");function q(){allSideMenu.forEach(e=>{const t=e.parentElement;e.addEventListener("click",function(){allSideMenu.forEach(n=>{n.parentElement.classList.remove("active")}),t.classList.add("active")})})}const menuBar=document.querySelector("#content nav .bx.bx-menu"),sidebar=document.getElementById("sidebar");function tog(){menuBar.addEventListener("click",function(){sidebar.classList.toggle("hide")})}const switchMode=document.getElementById("switch-mode");function dark(){switchMode.addEventListener("change",function(){this.checked?document.body.classList.add("dark"):document.body.classList.remove("dark")})}